SELECT T0.`id`, T0.`created_at`, T0.`updated_at`, T0.`deleted_at`, T0.`user_id`, T0.`auth_token`, T0.`last_login`, T1.`id`, T1.`created_at`, T1.`updated_at`, T1.`deleted_at`, T1.`name`, T1.`email`, T1.`password`, T1.`pin`, T1.`gender`, T1.`birthday`, T1.`profile_picture`, T1.`role`, T1.`admin_role`, T1.`phone`, T1.`subscribe`, T1.`locked`, T1.`last_announcement`, T1.`last_notification_at`, T1.`password_reset_token`, T1.`password_reset_expires`, T1.`setting_id` FROM `auth_tokens` T0 LEFT OUTER JOIN `users` T1 ON T1.`id` = T0.`user_id` WHERE T0.`deleted_at` IS NULL AND ( T0.`deleted_at` IS NULL AND T0.`auth_token` = ? AND T1.`deleted_at` IS NULL ) LIMIT 1